import OpenAI from "openai";
import type { ChatCompletionMessageParam } from "openai/resources/chat/completions";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const DEFAULT_MODEL = "gpt-4o";

export interface StreamingChatOptions {
  messages: ChatCompletionMessageParam[];
  model?: string;
  temperature?: number;
  maxTokens?: number;
  apiKey: string;
  onToken?: (token: string) => void;
  onComplete?: (fullResponse: string) => void;
  onError?: (error: Error) => void;
}

export interface ChatCompletionOptions {
  messages: ChatCompletionMessageParam[];
  model?: string;
  temperature?: number;
  maxTokens?: number;
  apiKey: string;
}

export class OpenAIService {
  private getClient(apiKey: string): OpenAI {
    return new OpenAI({ 
      apiKey,
      dangerouslyAllowBrowser: false // Server-side only
    });
  }

  async streamChatCompletion(options: StreamingChatOptions): Promise<void> {
    const {
      messages,
      model = DEFAULT_MODEL,
      temperature = 0.7,
      maxTokens = 2048,
      apiKey,
      onToken,
      onComplete,
      onError
    } = options;

    try {
      const openai = this.getClient(apiKey);

      const stream = await openai.chat.completions.create({
        model,
        messages,
        temperature: temperature / 100, // Convert from 0-100 to 0-1
        max_tokens: maxTokens,
        stream: true,
      });

      let fullResponse = "";

      for await (const chunk of stream) {
        const content = chunk.choices[0]?.delta?.content || "";
        if (content) {
          fullResponse += content;
          onToken?.(content);
        }
      }

      onComplete?.(fullResponse);
    } catch (error) {
      console.error("OpenAI streaming error:", error);
      onError?.(error as Error);
    }
  }

  async getChatCompletion(options: ChatCompletionOptions): Promise<string> {
    const {
      messages,
      model = DEFAULT_MODEL,
      temperature = 0.7,
      maxTokens = 2048,
      apiKey
    } = options;

    try {
      const openai = this.getClient(apiKey);

      const response = await openai.chat.completions.create({
        model,
        messages,
        temperature: temperature / 100, // Convert from 0-100 to 0-1
        max_tokens: maxTokens,
      });

      return response.choices[0]?.message?.content || "";
    } catch (error) {
      console.error("OpenAI completion error:", error);
      throw error;
    }
  }

  async generateImage(prompt: string, apiKey: string, options?: {
    size?: "1024x1024" | "1792x1024" | "1024x1792";
    quality?: "standard" | "hd";
    model?: "dall-e-2" | "dall-e-3";
  }): Promise<{ url: string }> {
    try {
      const openai = this.getClient(apiKey);

      const response = await openai.images.generate({
        model: options?.model || "dall-e-3",
        prompt,
        n: 1,
        size: options?.size || "1024x1024",
        quality: options?.quality || "standard",
      });

      return { url: response.data?.[0]?.url || "" };
    } catch (error) {
      console.error("OpenAI image generation error:", error);
      throw error;
    }
  }

  validateApiKey(apiKey: string): boolean {
    return apiKey.startsWith("sk-") && apiKey.length > 20;
  }

  getSupportedModels(): Array<{
    id: string;
    name: string;
    description: string;
    supportsImages: boolean;
    supportsFiles: boolean;
    contextWindow: number;
  }> {
    return [
      {
        id: "gpt-4o",
        name: "GPT-4o",
        description: "Latest model with enhanced capabilities",
        supportsImages: true,
        supportsFiles: true,
        contextWindow: 128000,
      },
      {
        id: "gpt-4-turbo",
        name: "GPT-4 Turbo",
        description: "Faster and more efficient",
        supportsImages: true,
        supportsFiles: true,
        contextWindow: 128000,
      },
      {
        id: "gpt-4",
        name: "GPT-4",
        description: "Standard GPT-4 model",
        supportsImages: false,
        supportsFiles: false,
        contextWindow: 8192,
      },
      {
        id: "gpt-3.5-turbo",
        name: "GPT-3.5 Turbo",
        description: "Fast and cost-effective",
        supportsImages: false,
        supportsFiles: false,
        contextWindow: 16385,
      },
    ];
  }
}

export const openaiService = new OpenAIService();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import MemoryStore from "memorystore";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const sessionStore = MemoryStore(session);
  return session({
    secret: process.env.SESSION_SECRET || 'fallback-session-secret-change-this',
    store: new sessionStore({
      checkPeriod: 86400000, // prune expired entries every 24h
    }),
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

