      }

      const redeemCode = await storage.getRedeemCode(code);
      if (!redeemCode) {
        return res.status(404).json({ message: "Invalid redeem code" });
      }

      if (redeemCode.isUsed) {
        return res.status(400).json({ message: "Redeem code has already been used" });
      }

      // Check if code is expired
      if (redeemCode.expiresAt && new Date() > redeemCode.expiresAt) {
        return res.status(400).json({ message: "Redeem code has expired" });
      }

      // Create subscription based on redeem code
      const expiresAt = new Date();
      expiresAt.setDate(expiresAt.getDate() + redeemCode.duration);

      const subscription = await storage.createSubscription({
        userId,
        planId: redeemCode.planId,
        status: "active",
        expiresAt,
      });

      // Mark redeem code as used
      await storage.useRedeemCode(code, userId);

      res.json({ subscription, message: "Redeem code applied successfully!" });
    } catch (error) {
      console.error("Error redeeming code:", error);
      res.status(500).json({ message: "Failed to redeem code" });
    }
  });

  // Contact message routes
  app.post('/api/contact', async (req, res) => {
    try {
      const contactData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(contactData);
      res.json({ 
        message: "Support ticket created successfully",
        ticketNumber: message.ticketNumber,
        id: message.id 
      });
    } catch (error) {
      console.error("Error creating contact message:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid contact data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  // Get user's tickets by email
  app.get("/api/contact/tickets/:email", async (req, res) => {
    try {
      const { email } = req.params;
      const tickets = await storage.getUserContactMessages(email);
      res.json(tickets);
    } catch (error) {
      console.error("Get user tickets error:", error);
      res.status(500).json({ message: "Failed to fetch tickets" });
    }
  });

  // Get ticket by ticket number (for users to track their tickets)
  app.get("/api/contact/ticket/:ticketNumber", async (req, res) => {
    try {
      const { ticketNumber } = req.params;
      const ticket = await storage.getContactMessageByTicket(ticketNumber);
      if (!ticket) {
        return res.status(404).json({ message: "Ticket not found" });
      }
      res.json(ticket);
    } catch (error) {
      console.error("Get ticket error:", error);
      res.status(500).json({ message: "Failed to fetch ticket" });
    }
  });

  // Usage tracking routes
  app.get('/api/usage', verifyFirebaseToken, async (req: any, res) => {
    try {
      const userId = req.user.uid;
      const { type, days } = req.query;

      let startDate;
      if (days) {
        startDate = new Date();
        startDate.setDate(startDate.getDate() - parseInt(days as string));
      }

      const usage = await storage.getUserUsage(userId, type as string, startDate);
      const dailyUsage = await storage.getUserDailyUsage(userId);

      res.json({
        usage,
        daily: dailyUsage,
      });
    } catch (error) {
      console.error("Error fetching usage:", error);
      res.status(500).json({ message: "Failed to fetch usage" });
    }
  });

  // Admin routes (protected by admin middleware)
  const adminAuth = async (req: any, res: any, next: any) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(401).json({ message: "Username and password required" });
      }

      const admin = await storage.getAdminUser(username);
      if (!admin) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Simple password comparison (in real app, use proper hashing comparison)
      const [hashed, salt] = admin.password.split(".");
      const hashedBuf = Buffer.from(hashed, "hex");
      const suppliedBuf = (await scryptAsync(password, salt, 64)) as Buffer;
      const isValid = timingSafeEqual(hashedBuf, suppliedBuf);

      if (!isValid) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      await storage.updateAdminLastLogin(username);
      req.admin = admin;
      next();
    } catch (error) {
      console.error("Admin auth error:", error);
      res.status(500).json({ message: "Authentication failed" });
    }
  };

  app.post('/api/admin/login', adminAuth, async (req: any, res) => {
    res.json({ message: "Login successful", admin: req.admin });
  });

  app.get('/api/admin/dashboard', async (req, res) => {
    try {
      // Get dashboard data
      const plans = await storage.getPlans();
      const subscriptions = await storage.getActiveSubscriptions();
      const contactMessages = await storage.getContactMessages();
      const redeemCodes = await storage.getUnusedRedeemCodes();

      res.json({
        stats: {
          totalPlans: plans.length,
          activeSubscriptions: subscriptions.length,
          pendingMessages: contactMessages.filter(m => m.status === "unread").length,
          unusedCodes: redeemCodes.length,
        },
        plans,
        subscriptions: subscriptions.slice(0, 10), // Recent 10
        contactMessages: contactMessages.slice(0, 10), // Recent 10
        redeemCodes: redeemCodes.slice(0, 10), // Recent 10
      });
    } catch (error) {
      console.error("Error fetching admin dashboard:", error);
      res.status(500).json({ message: "Failed to fetch dashboard data" });
    }
  });

  app.post('/api/admin/plans', async (req, res) => {
    try {
      const planData = insertPlanSchema.parse(req.body);
      const plan = await storage.createPlan(planData);
      res.json(plan);
    } catch (error) {
      console.error("Error creating plan:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid plan data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create plan" });
    }
  });

  app.post('/api/admin/redeem-codes', async (req, res) => {
    try {
      const { count = 1, ...codeData } = req.body;
      const codes = [];

      for (let i = 0; i < count; i++) {
        const code = Math.random().toString(36).substring(2, 15).toUpperCase();
        const redeemCode = await storage.createRedeemCode({
          ...insertRedeemCodeSchema.parse(codeData),
          code,
        });
        codes.push(redeemCode);
      }

      res.json({ codes, message: `${count} redeem codes created successfully` });
    } catch (error) {
      console.error("Error creating redeem codes:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid redeem code data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create redeem codes" });
    }
  });

  app.put('/api/admin/contact/:messageId', async (req, res) => {
    try {
      const { messageId } = req.params;
      const { status, adminReply } = req.body;

      const updatedMessage = await storage.updateContactMessage(messageId, {
        status,
        adminReply,
      });

      res.json(updatedMessage);
    } catch (error) {
      console.error("Error updating contact message:", error);
      res.status(500).json({ message: "Failed to update contact message" });
    }
  });

  // Health check
  app.get('/api/health', (req, res) => {
    res.json({ status: "OK", timestamp: new Date().toISOString() });
  });

  // Admin API routes
  app.get('/api/admin/stats', async (req, res) => {
    try {
      const totalUsers = await storage.getUserCount();
      const activeUsers = Math.floor(totalUsers * 0.7); // Estimate active users
      const totalChats = await storage.getChatCount();
      const totalMessages = await storage.getMessageCount();

      const stats = {
        totalUsers,
        activeUsers,
        totalChats,
        totalMessages,
        totalRevenue: 50000, // Example data
        monthlyRevenue: 12000, // Example data
      };

      res.json(stats);
    } catch (error) {
      console.error("Error fetching admin stats:", error);
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  app.get('/api/admin/users', async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      const usersWithSubscriptions = await Promise.all(
        users.map(async (user) => {
          const subscription = await storage.getUserSubscription(user.id);
          return {
            ...user,
            subscription: subscription ? "Premium" : "Free"
          };
        })
      );
      res.json(usersWithSubscriptions);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.get('/api/admin/redeem-codes', async (req, res) => {
    try {
      const codes = await storage.getUnusedRedeemCodes();
      res.json(codes);
    } catch (error) {
      console.error("Error fetching redeem codes:", error);
      res.status(500).json({ message: "Failed to fetch redeem codes" });
    }
  });

  app.post('/api/admin/redeem-codes/generate', async (req, res) => {
    try {
      const { planName, duration, durationType, count } = req.body;

      // Create a plan if it doesn't exist
      let plan;
      const plans = await storage.getPlans();
      plan = plans.find(p => p.name === planName);

      if (!plan) {
        // Create the plan
        plan = await storage.createPlan({
          name: planName,
          price: planName === 'Premium' ? 800 : 1500, // $8 or $15 in cents
          duration: 'monthly',
          features: JSON.stringify(['Unlimited chats', 'Premium models', 'Priority support']),
          isActive: true,
        });
      }

      const codes = [];
      for (let i = 0; i < count; i++) {
        const code = `${planName.toUpperCase()}-${Date.now()}-${Math.random().toString(36).substring(2, 8).toUpperCase()}`;
        const redeemCode = await storage.createRedeemCode({
          code,
          planId: plan.id,
          duration,
          durationType,
          isUsed: false,
          expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year expiry
        });
        codes.push(redeemCode);
      }

      res.json({ codes, message: `Generated ${count} redeem codes successfully` });
    } catch (error) {
      console.error("Error generating redeem codes:", error);
      res.status(500).json({ message: "Failed to generate redeem codes" });
    }
  });

  app.post('/api/admin/users/:userId/remove-premium', async (req, res) => {
    try {
      const { userId } = req.params;

      // Find and expire user's subscription
      const subscription = await storage.getUserSubscription(userId);
      if (subscription) {
        await storage.expireSubscription(subscription.id);
      }

      res.json({ message: "Premium access removed successfully" });
    } catch (error) {
      console.error("Error removing premium access:", error);
      res.status(500).json({ message: "Failed to remove premium access" });
    }
  });

  app.get('/api/admin/support-tickets', async (req, res) => {
    try {
      const tickets = await storage.getAllSupportTickets();
      res.json(tickets);
    } catch (error) {
      console.error("Error fetching support tickets:", error);
      res.status(500).json({ message: "Failed to fetch support tickets" });
    }
  });

  app.get('/api/admin/subscriptions', async (req, res) => {
    try {
      const subscriptions = await storage.getActiveSubscriptions();
      res.json(subscriptions);
    } catch (error) {
      console.error("Error fetching subscriptions:", error);
      res.status(500).json({ message: "Failed to fetch subscriptions" });
    }
  });

  const httpServer = createServer(app);

  // Only set up custom WebSocket in production to avoid conflicts with Vite's HMR WebSocket
  if (process.env.NODE_ENV === "production") {
    const wss = new WebSocketServer({ server: httpServer });

    wss.on('connection', (ws) => {
      console.log('WebSocket connection established');

      ws.on('message', (data) => {
        try {
          const message = JSON.parse(data.toString());
          // Handle WebSocket messages if needed for real-time features
          console.log('WebSocket message:', message);
        } catch (error) {
          console.error('WebSocket message error:', error);
        }
      });

      ws.on('close', () => {
        console.log('WebSocket connection closed');
      });
    });
  }

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  chats,
  messages,
  files,
  userSettings,
  artifacts,
  plans,
  subscriptions,
  redeemCodes,
  contactMessages,
  usageTracking,
  adminUsers,
  modelCapabilities,
  databaseBackups,

  type SelectUser,
  type InsertUser,
  type InsertChat,
  type SelectChat,
  type InsertMessage,
  type SelectMessage,
  type InsertFile,
  type SelectFile,
  type InsertUserSettings,
  type SelectUserSettings,
  type InsertArtifact,
  type SelectArtifact,
  type SelectPlan,
  type InsertPlan,
  type SelectSubscription,
  type InsertSubscription,
  type SelectRedeemCode,
  type InsertRedeemCode,
  type SelectContactMessage,
  type InsertContactMessage,
  type SelectUsageTracking,
  type InsertUsageTracking,
  type SelectAdminUser,
  type InsertAdminUser,
  type SelectModelCapability,
  type InsertModelCapability,
  type SelectDatabaseBackup,
  type InsertDatabaseBackup,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql } from "drizzle-orm";

// Type aliases for easier use
export type User = SelectUser;
export type Chat = SelectChat;
export type Message = SelectMessage;
export type File = SelectFile;
export type UserSettings = SelectUserSettings;
export type Artifact = SelectArtifact;
export type Plan = SelectPlan;
export type Subscription = SelectSubscription;
export type RedeemCode = SelectRedeemCode;
export type ContactMessage = SelectContactMessage;
export type UsageTracking = SelectUsageTracking;
export type AdminUser = SelectAdminUser;
export type ModelCapability = SelectModelCapability;
export type DatabaseBackup = SelectDatabaseBackup;

// Extended types for relations
export type ChatWithMessages = Chat & { messages: Message[] };
export type MessageWithFiles = Message & { files: File[] };
export type SubscriptionWithPlan = Subscription & { plan: Plan };
export type RedeemCodeWithPlan = RedeemCode & { plan: Plan };

// Interface for storage operations
export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: InsertUser): Promise<User>;

  // User settings operations
  getUserSettings(userId: string): Promise<UserSettings | undefined>;
  upsertUserSettings(userId: string, settings: Partial<InsertUserSettings>): Promise<UserSettings>;

  // Chat operations
  getUserChats(userId: string): Promise<Chat[]>;
  getChat(chatId: string): Promise<ChatWithMessages | undefined>;
  createChat(userId: string, data: InsertChat): Promise<Chat>;
  updateChat(chatId: string, data: Partial<InsertChat>): Promise<Chat>;
  deleteChat(chatId: string): Promise<void>;
  deleteUserChats(userId: string): Promise<void>;

  // Message operations
  getChatMessages(chatId: string): Promise<MessageWithFiles[]>;
  createMessage(data: InsertMessage): Promise<Message>;
  updateMessage(messageId: string, data: Partial<InsertMessage>): Promise<Message>;
  deleteMessage(messageId: string): Promise<void>;

  // File operations
  createFile(data: InsertFile): Promise<File>;
  getMessageFiles(messageId: string): Promise<File[]>;
  deleteFile(fileId: string): Promise<void>;

  // Artifact operations
  getChatArtifacts(chatId: string): Promise<Artifact[]>;
  getUserArtifacts(userId: string): Promise<Artifact[]>;
  createArtifact(data: InsertArtifact): Promise<Artifact>;
  updateArtifact(artifactId: string, data: Partial<InsertArtifact>): Promise<Artifact>;
  deleteArtifact(artifactId: string): Promise<void>;
  deleteUserArtifacts(userId: string): Promise<void>;

  // Premium plan operations
  getPlans(): Promise<Plan[]>;
  createPlan(data: InsertPlan): Promise<Plan>;
  updatePlan(planId: string, data: Partial<InsertPlan>): Promise<Plan>;
  deletePlan(planId: string): Promise<void>;

  // Subscription operations
  getUserSubscription(userId: string): Promise<SubscriptionWithPlan | undefined>;
  createSubscription(data: InsertSubscription): Promise<Subscription>;
  updateSubscription(subscriptionId: string, data: Partial<InsertSubscription>): Promise<Subscription>;
  expireSubscription(subscriptionId: string): Promise<void>;
  getActiveSubscriptions(): Promise<SubscriptionWithPlan[]>;

  // Redeem code operations
  getRedeemCode(code: string): Promise<RedeemCodeWithPlan | undefined>;
  createRedeemCode(data: InsertRedeemCode): Promise<RedeemCode>;
  useRedeemCode(code: string, userId: string): Promise<RedeemCode>;
  getUnusedRedeemCodes(): Promise<RedeemCodeWithPlan[]>;
  deleteRedeemCode(codeId: string): Promise<void>;

  // Contact message operations
  getContactMessages(): Promise<ContactMessage[]>;
  createContactMessage(data: InsertContactMessage): Promise<ContactMessage>;
  updateContactMessage(messageId: string, data: { status?: string, adminReply?: string }): Promise<ContactMessage>;
  deleteContactMessage(messageId: string): Promise<void>;

  // Usage tracking operations
  recordUsage(data: InsertUsageTracking): Promise<UsageTracking>;
  getUserUsage(userId: string, type?: string, startDate?: Date): Promise<UsageTracking[]>;
  getUserDailyUsage(userId: string, date?: Date): Promise<{ chat: number, image: number, token: number }>;

  // Admin operations
  getAdminUser(username: string): Promise<AdminUser | undefined>;
  createAdminUser(data: InsertAdminUser): Promise<AdminUser>;
  updateAdminLastLogin(username: string): Promise<void>;

  // Model capabilities operations
  getModelCapabilities(): Promise<ModelCapability[]>;
  getModelCapability(modelName: string): Promise<ModelCapability | undefined>;
  createModelCapability(data: InsertModelCapability): Promise<ModelCapability>;
  updateModelCapability(modelName: string, data: Partial<InsertModelCapability>): Promise<ModelCapability>;

  // Database backup operations
  createDatabaseBackup(data: InsertDatabaseBackup): Promise<DatabaseBackup>;
  getDatabaseBackups(): Promise<DatabaseBackup[]>;
  deleteDatabaseBackup(id: string): Promise<void>;

  // Data management
  deleteUserData(userId: string): Promise<void>;

  // Premium management
  cancelSubscription(subscriptionId: string): Promise<void>;
  removeUserPremium(userId: string): Promise<void>;

  // Auto-train functionality
  updateAutoTrainData(userId: string, content: string, interaction_type: string): Promise<void>;

  // Admin data access methods
  getAllUsers(): Promise<User[]>;
  getAllChats(): Promise<Chat[]>;
  getAllSubscriptions(): Promise<SubscriptionWithPlan[]>;
  getAllRedeemCodes(): Promise<RedeemCodeWithPlan[]>;
  getAllSupportTickets(): Promise<ContactMessage[]>;
  getAdminStats(): Promise<{
    totalUsers: number;
    activeUsers: number;
    totalChats: number;
    totalMessages: number;
    totalRevenue: number;
    monthlyRevenue: number;
    systemHealth: {
      cpuUsage: number;
      memoryUsage: number;
      diskUsage: number;
      uptime: string;
    };
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations (mandatory for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values({
        ...userData,
        createdAt: new Date(),
        updatedAt: new Date(),
      })
      .onConflictDoUpdate({
        target: users.id,
        set: {
          name: userData.name,
          email: userData.email,
          // Don't overwrite isAdmin field during upsert
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // User settings operations
  async getUserSettings(userId: string): Promise<UserSettings | undefined> {
    const [settings] = await db
      .select()
      .from(userSettings)
      .where(eq(userSettings.userId, userId));
    return settings;
  }

  async upsertUserSettings(userId: string, settings: Partial<InsertUserSettings>): Promise<UserSettings> {
    const [result] = await db
      .insert(userSettings)
      .values({ ...settings, userId })
      .onConflictDoUpdate({
        target: userSettings.userId,
        set: {
          ...settings,
          updatedAt: new Date(),
        },
      })
      .returning();
    return result;
  }

  // Chat operations
  async getUserChats(userId: string): Promise<Chat[]> {
    return await db
      .select()
      .from(chats)
      .where(eq(chats.userId, userId))
      .orderBy(desc(chats.updatedAt));
  }

  async getChat(chatId: string): Promise<ChatWithMessages | undefined> {
    // Get chat
    const [chat] = await db.select().from(chats).where(eq(chats.id, chatId));
    if (!chat) return undefined;

    // Get messages with files
    const chatMessages = await this.getChatMessages(chatId);

    // Get files
    const chatFiles = await db
      .select()
      .from(files)
      .where(eq(files.chatId, chatId));

    // Get artifacts
    const chatArtifacts = await this.getChatArtifacts(chatId);

    return {
      ...chat,
      messages: chatMessages,
      files: chatFiles,
      artifacts: chatArtifacts,
    };
  }

  async createChat(userId: string, data: InsertChat): Promise<Chat> {
    const [chat] = await db
      .insert(chats)
      .values({ ...data, userId })
      .returning();
    return chat;
  }

  async updateChat(chatId: string, data: Partial<InsertChat>): Promise<Chat> {
    const [chat] = await db
      .update(chats)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(chats.id, chatId))
      .returning();
    return chat;
  }

  async deleteChat(chatId: string): Promise<void> {
    await db.delete(chats).where(eq(chats.id, chatId));
  }

  async deleteUserChats(userId: string): Promise<void> {
    await db.delete(chats).where(eq(chats.userId, userId));
  }

  // Message operations
  async getChatMessages(chatId: string): Promise<MessageWithFiles[]> {
    const messagesData = await db
      .select()
      .from(messages)
      .where(eq(messages.chatId, chatId))
      .orderBy(messages.timestamp);

    // Get files for each message
    const messagesWithFiles = await Promise.all(
      messagesData.map(async (message) => {
        const messageFiles = await this.getMessageFiles(message.id);
        const messageArtifacts = await db
          .select()
          .from(artifacts)
          .where(eq(artifacts.messageId, message.id));

        return {
          ...message,
          files: messageFiles,
          artifacts: messageArtifacts,
        };
      })
    );

    return messagesWithFiles;
  }

  async createMessage(data: InsertMessage): Promise<Message> {
    const [message] = await db
      .insert(messages)
      .values(data)
      .returning();
    return message;
  }

  async updateMessage(messageId: string, data: Partial<InsertMessage>): Promise<Message> {
    const [message] = await db
      .update(messages)
      .set({ ...data, isEdited: true })
      .where(eq(messages.id, messageId))
      .returning();
    return message;
  }

  async deleteMessage(messageId: string): Promise<void> {
    await db.delete(messages).where(eq(messages.id, messageId));
  }

  // File operations
  async createFile(data: InsertFile): Promise<File> {
    const [file] = await db
      .insert(files)
      .values(data)
      .returning();
    return file;
  }

  async getMessageFiles(messageId: string): Promise<File[]> {
    return await db
      .select()
      .from(files)
      .where(eq(files.messageId, messageId));
  }

  async deleteFile(fileId: string): Promise<void> {
    await db.delete(files).where(eq(files.id, fileId));
  }

  // Artifact operations
  async getChatArtifacts(chatId: string): Promise<Artifact[]> {
    return await db
      .select()
      .from(artifacts)
      .where(eq(artifacts.chatId, chatId))
      .orderBy(desc(artifacts.createdAt));
  }

  async getUserArtifacts(userId: string): Promise<Artifact[]> {
    return await db
      .select({
        id: artifacts.id,
        chatId: artifacts.chatId,
        messageId: artifacts.messageId,
        fileName: artifacts.fileName,
        content: artifacts.content,
        version: artifacts.version,
        type: artifacts.type,
        language: artifacts.language,
        linkedArtifactId: artifacts.linkedArtifactId,
        createdAt: artifacts.createdAt,
      })
      .from(artifacts)
      .innerJoin(chats, eq(artifacts.chatId, chats.id))
      .where(eq(chats.userId, userId))
      .orderBy(desc(artifacts.createdAt));
  }

  async createArtifact(data: InsertArtifact): Promise<Artifact> {
    const [artifact] = await db
      .insert(artifacts)
      .values(data)
      .returning();
    return artifact;
  }

  async updateArtifact(artifactId: string, data: Partial<InsertArtifact>): Promise<Artifact> {
    const [artifact] = await db
      .update(artifacts)
      .set(data)
      .where(eq(artifacts.id, artifactId))
      .returning();
    return artifact;
  }

  async deleteArtifact(artifactId: string): Promise<void> {
    await db.delete(artifacts).where(eq(artifacts.id, artifactId));
  }

  async deleteUserArtifacts(userId: string): Promise<void> {
    await db
      .delete(artifacts)
      .where(
        sql`${artifacts.chatId} IN (SELECT id FROM ${chats} WHERE user_id = ${userId})`
      );
  }

  // Premium plan operations
  async getPlans(): Promise<Plan[]> {
    const rawPlans = await db
      .select()
      .from(plans)
      .where(eq(plans.isActive, true))
      .orderBy(plans.price);

    // Convert features from JSON string to array
    return rawPlans.map(plan => ({
      ...plan,
      features: typeof plan.features === 'string' ? JSON.parse(plan.features) : plan.features
    }));
  }

  async createPlan(data: InsertPlan): Promise<Plan> {
    // Convert features array to JSON string for SQLite
    const planData = {
      ...data,
      features: typeof data.features === 'string' ? data.features : JSON.stringify(data.features)
    };

    const [plan] = await db
      .insert(plans)
      .values(planData)
      .returning();

    // Convert features back to array when returning
    return {
      ...plan,
      features: typeof plan.features === 'string' ? JSON.parse(plan.features) : plan.features
    };
  }

  async updatePlan(planId: string, data: Partial<InsertPlan>): Promise<Plan> {
    // Convert features array to JSON string for SQLite if provided
    const updateData = data.features 
      ? { ...data, features: typeof data.features === 'string' ? data.features : JSON.stringify(data.features) }
      : data;

    const [plan] = await db
      .update(plans)
      .set(updateData)
      .where(eq(plans.id, planId))
      .returning();

    // Convert features back to array when returning
    return {
      ...plan,
      features: typeof plan.features === 'string' ? JSON.parse(plan.features) : plan.features
    };
  }

  async deletePlan(planId: string): Promise<void> {
    await db.delete(plans).where(eq(plans.id, planId));
