  // Redeem code operations
  async getRedeemCode(code: string): Promise<RedeemCodeWithPlan | undefined> {
    const [redeemCode] = await db
      .select({
        id: redeemCodes.id,
        code: redeemCodes.code,
        planId: redeemCodes.planId,
        duration: redeemCodes.duration,
        isUsed: redeemCodes.isUsed,
        usedBy: redeemCodes.usedBy,
        usedAt: redeemCodes.usedAt,
        createdAt: redeemCodes.createdAt,
        expiresAt: redeemCodes.expiresAt,
        plan: plans,
      })
      .from(redeemCodes)
      .innerJoin(plans, eq(redeemCodes.planId, plans.id))
      .where(eq(redeemCodes.code, code));

    return redeemCode as RedeemCodeWithPlan;
  }

  async createRedeemCode(data: InsertRedeemCode): Promise<RedeemCode> {
    const [redeemCode] = await db
      .insert(redeemCodes)
      .values(data)
      .returning();
    return redeemCode;
  }

  async useRedeemCode(code: string, userId: string): Promise<RedeemCode> {
    const [redeemCode] = await db
      .update(redeemCodes)
      .set({ 
        isUsed: true, 
        usedBy: userId, 
        usedAt: new Date() 
      })
      .where(eq(redeemCodes.code, code))
      .returning();
    return redeemCode;
  }

  async getUnusedRedeemCodes(): Promise<RedeemCodeWithPlan[]> {
    return await db
      .select({
        id: redeemCodes.id,
        code: redeemCodes.code,
        planId: redeemCodes.planId,
        duration: redeemCodes.duration,
        isUsed: redeemCodes.isUsed,
        usedBy: redeemCodes.usedBy,
        usedAt: redeemCodes.usedAt,
        createdAt: redeemCodes.createdAt,
        expiresAt: redeemCodes.expiresAt,
        plan: plans,
      })
      .from(redeemCodes)
      .innerJoin(plans, eq(redeemCodes.planId, plans.id))
      .where(eq(redeemCodes.isUsed, false))
      .orderBy(desc(redeemCodes.createdAt)) as RedeemCodeWithPlan[];
  }

  async deleteRedeemCode(codeId: string): Promise<void> {
    await db.delete(redeemCodes).where(eq(redeemCodes.id, codeId));
  }

  async redeemCode(userId: string, code: string): Promise<{ subscription: Subscription; message: string }> {
    const redeemCode = await this.getRedeemCode(code);
    if (!redeemCode) {
      throw new Error("Invalid redeem code");
    }

    if (redeemCode.isUsed) {
      throw new Error("Redeem code has already been used");
    }

    // Check if code is expired
    if (redeemCode.expiresAt && new Date() > redeemCode.expiresAt) {
      throw new Error("Redeem code has expired");
    }

    // Create subscription based on redeem code
    const expiresAt = new Date();
    if (redeemCode.durationType === "years") {
      expiresAt.setFullYear(expiresAt.getFullYear() + redeemCode.duration);
    } else {
      expiresAt.setMonth(expiresAt.getMonth() + redeemCode.duration);
    }

    const subscription = await this.createSubscription({
      userId,
      planId: redeemCode.planId,
      status: "active",
      expiresAt,
    });

    // Mark redeem code as used
    await this.useRedeemCode(code, userId);

    return { subscription, message: "Redeem code applied successfully!" };
  }

