import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Sidebar } from "./Sidebar";
import { MessageList } from "./MessageList";
import { MessageInput } from "./MessageInput";
import { ModelSelector } from "./ModelSelector";
import { SettingsModal } from "./SettingsModal";
import { useChatContext } from "@/contexts/ChatContext";
import { useIsMobile } from "@/hooks/use-mobile";
import { cn } from "@/lib/utils";
import { Menu, Bot, Brain, Moon, Sun } from "lucide-react";

interface ChatInterfaceProps {
  className?: string;
}

export function ChatInterface({ className }: ChatInterfaceProps) {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [modelSelectorOpen, setModelSelectorOpen] = useState(false);
  const [settingsOpen, setSettingsOpen] = useState(false);
  const isMobile = useIsMobile();

  const { currentChat, actualTheme, toggleTheme, user } = useChatContext();

  const getCurrentModel = () => {
    return currentChat?.model || user?.settings?.defaultModel || "gpt-4o";
  };

  const getModelDisplayName = (modelId: string) => {
    const modelNames: Record<string, string> = {
      "gpt-4o": "GPT-4o",
      "gpt-4-turbo": "GPT-4 Turbo", 
      "gpt-4": "GPT-4",
      "gpt-3.5-turbo": "GPT-3.5 Turbo",
    };
    return modelNames[modelId] || modelId;
  };

  return (
    <div className={cn("flex h-screen overflow-hidden", className)}>
      {/* Sidebar */}
      <Sidebar
        isOpen={sidebarOpen || !isMobile}
        onToggle={() => setSidebarOpen(!sidebarOpen)}
        onSettingsOpen={() => setSettingsOpen(true)}
        className={cn(!isMobile && "relative")}
      />

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col min-w-0">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-border bg-background">
          <div className="flex items-center gap-3">
            {/* Mobile menu button */}
            {isMobile && (
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSidebarOpen(true)}
              >
                <Menu className="h-5 w-5" />
              </Button>
            )}

            <h1 className="text-lg font-semibold">ChatGPT</h1>

            {/* Current Model Badge */}
            <Badge variant="secondary" className="flex items-center gap-2">
              <Bot className="h-3 w-3 text-primary" />
              <span>{getModelDisplayName(getCurrentModel())}</span>
            </Badge>
          </div>

          <div className="flex items-center gap-2">
            {/* Model Selector */}
            <Button
              variant="ghost"
              onClick={() => setModelSelectorOpen(true)}
              className="flex items-center gap-2"
            >
              <Brain className="h-4 w-4" />
              <span className="hidden sm:inline">Model</span>
            </Button>

            {/* Theme Toggle */}
            <Button
              variant="ghost"
              size="icon"
              onClick={toggleTheme}
            >
              {actualTheme === "dark" ? (
                <Sun className="h-4 w-4" />
              ) : (
                <Moon className="h-4 w-4" />
              )}
            </Button>
          </div>
        </div>

        {/* Messages */}
        <MessageList className="flex-1" />

        {/* Input */}
        <MessageInput />
      </div>

      {/* Modals */}
      <ModelSelector
        open={modelSelectorOpen}
        onOpenChange={setModelSelectorOpen}
        currentModel={getCurrentModel()}
      />

      <SettingsModal
        open={settingsOpen}
        onOpenChange={setSettingsOpen}
      />
    </div>
  );
}

client/src/components/FileUpload.tsx.content:
import React, { useState, useCallback, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";
import { Upload, X, FileText, Image as ImageIcon, File } from "lucide-react";

interface FileUploadProps {
  onFileSelect: (files: FileList) => void;
  onCancel: () => void;
  maxFiles?: number;
  maxSize?: number; // in bytes
  acceptedTypes?: string[];
  className?: string;
}

export function FileUpload({
  onFileSelect,
  onCancel,
  maxFiles = 5,
  maxSize = 10 * 1024 * 1024, // 10MB default
  acceptedTypes = ["*/*"],
  className,
}: FileUploadProps) {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const validateFiles = (files: File[]): File[] => {
    const validFiles: File[] = [];

    for (const file of files) {
      // Check file size
      if (file.size > maxSize) {
        toast({
          title: "File too large",
          description: `${file.name} is larger than ${formatFileSize(maxSize)}`,
          variant: "destructive",
        });
        continue;
      }

      // Check file type if acceptedTypes is specified and not wildcard
      if (acceptedTypes.length > 0 && !acceptedTypes.includes("*/*")) {
        const isAccepted = acceptedTypes.some(type => {
          if (type.endsWith("/*")) {
            return file.type.startsWith(type.slice(0, -1));
          }
          return file.type === type || file.name.toLowerCase().endsWith(type);
        });

        if (!isAccepted) {
          toast({
            title: "File type not supported",
            description: `${file.name} is not a supported file type`,
            variant: "destructive",
          });
          continue;
        }
      }

      validFiles.push(file);
    }

    // Check max files limit
    if (validFiles.length > maxFiles) {
      toast({
        title: "Too many files",
        description: `You can only upload up to ${maxFiles} files at once`,
        variant: "destructive",
      });
      return validFiles.slice(0, maxFiles);
    }

    return validFiles;
  };

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = Array.from(e.dataTransfer.files);
    const validFiles = validateFiles(files);

    if (validFiles.length > 0) {
      setSelectedFiles(validFiles);
    }
  }, [maxFiles, maxSize, acceptedTypes, toast]);

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files);
      const validFiles = validateFiles(files);

      if (validFiles.length > 0) {
        setSelectedFiles(validFiles);
      }
    }
  };

  const handleRemoveFile = (index: number) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleConfirm = () => {
    if (selectedFiles.length > 0) {
      const dt = new DataTransfer();
      selectedFiles.forEach(file => dt.items.add(file));
      onFileSelect(dt.files);
    }
  };

  const getFileIcon = (file: File) => {
    if (file.type.startsWith("image/")) return ImageIcon;
    if (file.type.startsWith("text/")) return FileText;
    return File;
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + " " + sizes[i];
  };

  return (
    <Card className={cn("w-full", className)}>
      <CardContent className="p-6">
        {/* Drop Zone */}
        <div
          className={cn(
            "border-2 border-dashed rounded-lg p-8 text-center transition-colors cursor-pointer",
            dragActive
              ? "border-primary bg-primary/5"
              : "border-muted-foreground/25 hover:border-primary/50 hover:bg-muted/50"
          )}
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
          onClick={() => fileInputRef.current?.click()}
        >
          <Upload className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
          <div className="space-y-2">
            <p className="text-lg font-medium">
              Drag and drop files here
            </p>
            <p className="text-sm text-muted-foreground">
              or click to browse files
            </p>
            <p className="text-xs text-muted-foreground">
              Max {maxFiles} files, up to {formatFileSize(maxSize)} each
            </p>
          </div>
        </div>

        {/* Selected Files */}
        {selectedFiles.length > 0 && (
          <div className="mt-6 space-y-3">
            <h3 className="font-medium">Selected Files</h3>
            <div className="space-y-2">
              {selectedFiles.map((file, index) => {
                const IconComponent = getFileIcon(file);
                return (
                  <div
                    key={index}
                    className="flex items-center justify-between p-3 bg-muted rounded-lg"
                  >
                    <div className="flex items-center gap-3">
                      <IconComponent className="h-5 w-5 text-muted-foreground" />
                      <div>
                        <p className="text-sm font-medium">{file.name}</p>
                        <p className="text-xs text-muted-foreground">
                          {formatFileSize(file.size)}
                        </p>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleRemoveFile(index)}
                      className="h-8 w-8"
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Actions */}
        <div className="flex justify-end gap-3 mt-6">
          <Button variant="outline" onClick={onCancel}>
            Cancel
          </Button>
          <Button
            onClick={handleConfirm}
            disabled={selectedFiles.length === 0}
          >
            Upload {selectedFiles.length > 0 ? `${selectedFiles.length} file${selectedFiles.length > 1 ? 's' : ''}` : ''}
          </Button>
        </div>

        {/* Hidden file input */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          accept={acceptedTypes.join(",")}
          onChange={handleFileInput}
          className="hidden"
        />
      </CardContent>
    </Card>
  );
}

client/src/components/MessageActions.tsx.content:
import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useChatContext } from "@/contexts/ChatContext";
import { 
  Copy, 
  RotateCcw, 
  Share2, 
  Edit3, 
  Download, 
  Image,
  MoreHorizontal,
  Check
} from "lucide-react";
import type { ChatMessage } from "@/hooks/useChat";

interface MessageActionsProps {
  message: ChatMessage;
}

export function MessageActions({ message }: MessageActionsProps) {
  const { editMessage, regenerateMessage } = useChatContext();
  const [isEditing, setIsEditing] = useState(false);
  const [editContent, setEditContent] = useState(message.content);
  const [shareDialogOpen, setShareDialogOpen] = useState(false);
  const [copied, setCopied] = useState(false);
  const { toast } = useToast();

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(message.content);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
      toast({
        title: "Copied!",
        description: "Message copied to clipboard.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy message.",
        variant: "destructive",
      });
    }
  };

  const handleEdit = async () => {
    if (editContent.trim() !== message.content) {
      await editMessage(message.id, editContent.trim());
    }
    setIsEditing(false);
  };

  const handleRegenerate = async () => {
    await regenerateMessage(message.id);
  };

  const handleShare = () => {
    setShareDialogOpen(true);
  };

  const handleShareAction = async (action: "link" | "txt" | "image") => {
    switch (action) {
      case "link":
        // Create a shareable link (this would be implemented with a backend endpoint)
        const shareUrl = `${window.location.origin}/share/${message.id}`;
        await navigator.clipboard.writeText(shareUrl);
        toast({
          title: "Link copied!",
          description: "Share link copied to clipboard.",
        });
        break;

      case "txt":
        // Download as text file
        const blob = new Blob([message.content], { type: "text/plain" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `message-${message.id}.txt`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
        break;

      case "image":
        // Export as image (would need canvas implementation)
        toast({
          title: "Coming Soon",
          description: "Image export will be available soon.",
        });
        break;
    }
    setShareDialogOpen(false);
  };

  const isUser = message.role === "user";
  const isAssistant = message.role === "assistant";

  if (isEditing && isUser) {
    return (
      <div className="mt-4 space-y-3">
        <Textarea
          value={editContent}
          onChange={(e) => setEditContent(e.target.value)}
          className="min-h-[100px] resize-none"
          placeholder="Edit your message..."
        />
        <div className="flex gap-2">
          <Button size="sm" onClick={handleEdit}>
            Save
          </Button>
          <Button 
            size="sm" 
            variant="outline" 
            onClick={() => {
              setIsEditing(false);
              setEditContent(message.content);
            }}
          >
            Cancel
          </Button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="flex items-center gap-2 mt-4">
        {/* Copy */}
        <Button
          variant="ghost"
          size="sm"
          onClick={handleCopy}
          className="h-8 px-2 text-xs"
        >
          {copied ? (
            <Check className="h-3 w-3 mr-1" />
          ) : (
            <Copy className="h-3 w-3 mr-1" />
          )}
          {copied ? "Copied!" : "Copy"}
        </Button>

        {/* Edit (for user messages) */}
        {isUser && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsEditing(true)}
            className="h-8 px-2 text-xs"
          >
            <Edit3 className="h-3 w-3 mr-1" />
            Edit
          </Button>
        )}

        {/* Regenerate (for assistant messages) */}
        {isAssistant && (
          <Button
            variant="ghost"
            size="sm"
            onClick={handleRegenerate}
            className="h-8 px-2 text-xs"
          >
            <RotateCcw className="h-3 w-3 mr-1" />
            Regenerate
          </Button>
        )}

        {/* Share */}
        <Button
          variant="ghost"
          size="sm"
          onClick={handleShare}
          className="h-8 px-2 text-xs"
        >
          <Share2 className="h-3 w-3 mr-1" />
          Share
        </Button>

        {/* More actions */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="sm" className="h-8 px-2">
              <MoreHorizontal className="h-3 w-3" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            <DropdownMenuItem onClick={handleCopy}>
              <Copy className="h-4 w-4 mr-2" />
              Copy message
            </DropdownMenuItem>
            {isAssistant && (
              <DropdownMenuItem onClick={handleRegenerate}>
                <RotateCcw className="h-4 w-4 mr-2" />
                Regenerate response
              </DropdownMenuItem>
            )}
            <DropdownMenuItem onClick={handleShare}>
              <Share2 className="h-4 w-4 mr-2" />
              Share message
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Share Dialog */}
      <Dialog open={shareDialogOpen} onOpenChange={setShareDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Share Message</DialogTitle>
            <DialogDescription>
              Choose how you'd like to share this message.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-3">
            <Button
              variant="outline"
              className="w-full justify-start"
              onClick={() => handleShareAction("link")}
            >
              <Share2 className="h-4 w-4 mr-3 text-blue-500" />
              Copy message link
            </Button>
            <Button
              variant="outline"
              className="w-full justify-start"
              onClick={() => handleShareAction("txt")}
            >
              <Download className="h-4 w-4 mr-3 text-green-500" />
              Download as .txt
            </Button>
            <Button
              variant="outline"
              className="w-full justify-start"
              onClick={() => handleShareAction("image")}
            >
              <Image className="h-4 w-4 mr-3 text-purple-500" />
              Export as image
            </Button>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShareDialogOpen(false)}>
              Cancel
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/MessageInput.tsx.content:
import React, { useState, useRef, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { FileUpload } from "./FileUpload";
import { useChatContext } from "@/contexts/ChatContext";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";
import { 
  Send, 
  Square, 
  Paperclip, 
  X,
  FileText,
  Image as ImageIcon,
  File
} from "lucide-react";

interface MessageInputProps {
  className?: string;
}

export function MessageInput({ className }: MessageInputProps) {
  const { sendMessage, isStreaming, stopGeneration } = useChatContext();
  const [input, setInput] = useState("");
  const [files, setFiles] = useState<FileList | null>(null);
  const [showFileUpload, setShowFileUpload] = useState(false);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleInputChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setInput(e.target.value);
    autoResize();
  };

  const autoResize = useCallback(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = Math.min(textarea.scrollHeight, 128) + "px";
    }
  }, []);

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleSend = async () => {
    const trimmedInput = input.trim();
    if (!trimmedInput && !files) return;

    try {
      await sendMessage(trimmedInput, files || undefined);
      setInput("");
      setFiles(null);
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleFileSelect = (selectedFiles: FileList) => {
    setFiles(selectedFiles);
    setShowFileUpload(false);
  };

  const handleFileRemove = (index: number) => {
    if (!files) return;

    const dt = new DataTransfer();
    Array.from(files).forEach((file, i) => {
      if (i !== index) dt.items.add(file);
    });

    setFiles(dt.files.length > 0 ? dt.files : null);
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType.startsWith("image/")) return ImageIcon;
    if (mimeType.startsWith("text/")) return FileText;
    return File;
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + " " + sizes[i];
  };

  const canSend = input.trim() || files;

  return (
    <div className={cn("border-t border-border bg-background p-4", className)}>
      <div className="max-w-3xl mx-auto">
        {/* File Upload Modal */}
        {showFileUpload && (
          <div className="mb-4">
            <FileUpload
              onFileSelect={handleFileSelect}
              onCancel={() => setShowFileUpload(false)}
              maxFiles={5}
              maxSize={10 * 1024 * 1024} // 10MB
            />
          </div>
        )}

        {/* File Preview */}
        {files && files.length > 0 && (
          <div className="mb-4 p-3 bg-muted rounded-lg border border-dashed">
            <div className="space-y-2">
              {Array.from(files).map((file, index) => {
                const IconComponent = getFileIcon(file.type);
                return (
                  <div key={index} className="flex items-center justify-between p-2 bg-background rounded">
                    <div className="flex items-center gap-3">
                      <IconComponent className="h-4 w-4 text-muted-foreground" />
                      <div>
                        <p className="text-sm font-medium">{file.name}</p>
                        <p className="text-xs text-muted-foreground">
                          {formatFileSize(file.size)}
                        </p>
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleFileRemove(index)}
                      className="h-6 w-6"
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Input Area */}
        <div className="relative">
          <div className="flex items-end gap-3 p-3 bg-muted rounded-xl border">
            {/* File Upload Button */}
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setShowFileUpload(true)}
              className="flex-shrink-0 text-muted-foreground hover:text-foreground"
              disabled={isStreaming}
            >
              <Paperclip className="h-4 w-4" />
            </Button>

            {/* Text Input */}
            <div className="flex-1 min-w-0">
              <Textarea
                ref={textareaRef}
                value={input}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
                placeholder="Message ChatGPT..."
                className="min-h-[40px] max-h-32 resize-none border-none bg-transparent p-0 text-sm placeholder:text-muted-foreground focus-visible:ring-0 focus-visible:ring-offset-0"
                disabled={isStreaming}
              />
            </div>

            {/* Send/Stop Button */}
            <Button
              onClick={isStreaming ? stopGeneration : handleSend}
              disabled={!canSend && !isStreaming}
              className={cn(
                "flex-shrink-0 transition-all duration-200",
                isStreaming 
                  ? "bg-destructive hover:bg-destructive/90 text-destructive-foreground" 
                  : "bg-primary hover:bg-primary/90 text-primary-foreground"
              )}
              size="icon"
            >
              {isStreaming ? (
                <Square className="h-4 w-4" />
              ) : (
                <Send className="h-4 w-4" />
              )}
            </Button>
          </div>

          {/* Helper Text */}
          <div className="flex items-center justify-center mt-2 text-xs text-muted-foreground">
            <span>ChatGPT can make mistakes. Check important info.</span>
          </div>
        </div>
      </div>

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        multiple
        className="hidden"
        onChange={(e) => {
          if (e.target.files) {
            handleFileSelect(e.target.files);
          }
        }}
      />
    </div>
  );
}

