import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { toast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Database, 
  Download, 
  Upload, 
  Trash2, 
  AlertTriangle,
  HardDrive,
  Clock,
  FileText,
  Eye
} from "lucide-react";
import { formatBytes, formatDate } from "@/lib/utils";

interface DatabaseBackup {
  id: string;
  fileName: string;
  fileSize: number;
  filePath: string;
  backupType: "full" | "incremental";
  createdAt: string;
}

interface DatabaseStats {
  totalTables: number;
  totalRows: number;
  databaseSize: string;
  lastBackup?: string;
}

export function DatabaseManager() {
  const [viewData, setViewData] = useState<any>(null);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const queryClient = useQueryClient();

  const { data: backups, isLoading: backupsLoading } = useQuery({
    queryKey: ["/api/admin/database/backups"],
  });

  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/admin/database/stats"],
  });

  const createBackupMutation = useMutation({
    mutationFn: async (type: "full" | "incremental") => {
      return await apiRequest("/api/admin/database/backup", {
        method: "POST",
        body: JSON.stringify({ type }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/database/backups"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/database/stats"] });
      toast({
        title: "Success",
        description: "Database backup created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to create database backup",
        variant: "destructive",
      });
    },
  });

  const downloadBackupMutation = useMutation({
    mutationFn: async (backupId: string) => {
      const response = await fetch(`/api/admin/database/backup/${backupId}/download`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to download backup");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `database-backup-${new Date().toISOString().split('T')[0]}.sql`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Database backup downloaded successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to download database backup",
        variant: "destructive",
      });
    },
  });

  const deleteBackupMutation = useMutation({
    mutationFn: async (backupId: string) => {
      return await apiRequest(`/api/admin/database/backup/${backupId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/database/backups"] });
      toast({
        title: "Success",
        description: "Database backup deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to delete database backup",
        variant: "destructive",
      });
    },
  });

  const viewDatabaseMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("/api/admin/database/view", {
        method: "GET",
      });
    },
    onSuccess: (data) => {
      setViewData(data);
      setIsViewDialogOpen(true);
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to fetch database data",
        variant: "destructive",
      });
    },
  });

  return (
    <div className="space-y-6">
      {/* Database Statistics */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Database className="h-5 w-5" />
            Database Overview
          </CardTitle>
        </CardHeader>
        <CardContent>
          {statsLoading ? (
            <div className="animate-pulse space-y-4">
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/3"></div>
            </div>
          ) : (
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">{stats?.totalTables || 0}</div>
                <div className="text-sm text-muted-foreground">Tables</div>
              </div>
              <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                <div className="text-2xl font-bold text-green-600 dark:text-green-400">{stats?.totalRows || 0}</div>
                <div className="text-sm text-muted-foreground">Total Records</div>
              </div>
              <div className="text-center p-4 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg">
                <div className="text-2xl font-bold text-yellow-600 dark:text-yellow-400">{stats?.databaseSize || "0 MB"}</div>
                <div className="text-sm text-muted-foreground">Database Size</div>
              </div>
              <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                <div className="text-2xl font-bold text-purple-600 dark:text-purple-400">{backups?.length || 0}</div>
                <div className="text-sm text-muted-foreground">Backups</div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Database Actions */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <HardDrive className="h-5 w-5" />
            Database Management
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-4">
            <Button 
              onClick={() => createBackupMutation.mutate("full")}
              disabled={createBackupMutation.isPending}
              className="flex items-center gap-2"
              data-testid="button-create-full-backup"
            >
              <Download className="h-4 w-4" />
              Create Full Backup
            </Button>

            <Button 
              onClick={() => createBackupMutation.mutate("incremental")}
              disabled={createBackupMutation.isPending}
              variant="outline"
              className="flex items-center gap-2"
              data-testid="button-create-incremental-backup"
            >
              <Download className="h-4 w-4" />
              Create Incremental Backup
            </Button>

            <Button 
              onClick={() => viewDatabaseMutation.mutate()}
              disabled={viewDatabaseMutation.isPending}
              variant="outline"
              className="flex items-center gap-2"
              data-testid="button-view-database"
            >
              <Eye className="h-4 w-4" />
              View Database
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Backup History */}
      <Card>
        <CardHeader>
          <CardTitle>Backup History</CardTitle>
        </CardHeader>
        <CardContent>
          {backupsLoading ? (
            <div className="animate-pulse space-y-4">
              {[1, 2, 3].map(i => (
                <div key={i} className="h-16 bg-gray-200 dark:bg-gray-700 rounded"></div>
              ))}
            </div>
          ) : !backups?.length ? (
            <div className="text-center py-8 text-muted-foreground">
              <Database className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No backups found. Create your first backup above.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {backups.map((backup: DatabaseBackup) => (
                <div key={backup.id} className="flex items-center justify-between p-4 border rounded-lg" data-testid={`backup-${backup.id}`}>
                  <div className="flex items-center gap-4">
                    <FileText className="h-8 w-8 text-blue-500" />
                    <div>
                      <div className="font-semibold">{backup.fileName}</div>
                      <div className="text-sm text-muted-foreground">
                        {formatBytes(backup.fileSize)} â€¢ {formatDate(backup.createdAt)}
                      </div>
                    </div>
                    <Badge variant={backup.backupType === "full" ? "default" : "secondary"}>
                      {backup.backupType}
                    </Badge>
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => downloadBackupMutation.mutate(backup.id)}
                      disabled={downloadBackupMutation.isPending}
                      data-testid={`button-download-${backup.id}`}
                    >
                      <Download className="h-4 w-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => deleteBackupMutation.mutate(backup.id)}
                      disabled={deleteBackupMutation.isPending}
                      data-testid={`button-delete-${backup.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* View Database Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Database Structure & Data</DialogTitle>
            <DialogDescription>
              Overview of your database tables and sample data
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-6">
            {viewData && Object.entries(viewData).map(([tableName, tableData]: [string, any]) => (
              <div key={tableName} className="border rounded-lg p-4">
                <h3 className="font-semibold text-lg mb-2 capitalize">{tableName.replace('_', ' ')}</h3>
                <div className="text-sm text-muted-foreground mb-4">
                  {Array.isArray(tableData) ? `${tableData.length} records` : 'No data'}
                </div>
                {Array.isArray(tableData) && tableData.length > 0 && (
                  <div className="overflow-x-auto">
                    <table className="w-full text-sm border-collapse">
                      <thead>
                        <tr className="border-b">
                          {Object.keys(tableData[0]).map(key => (
                            <th key={key} className="text-left p-2 font-medium">{key}</th>
                          ))}
                        </tr>
                      </thead>
                      <tbody>
                        {tableData.slice(0, 5).map((row: any, idx: number) => (
                          <tr key={idx} className="border-b">
                            {Object.values(row).map((value: any, valueIdx: number) => (
                              <td key={valueIdx} className="p-2 truncate max-w-[200px]">
                                {typeof value === 'object' ? JSON.stringify(value) : String(value || '-')}
                              </td>
                            ))}
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    {tableData.length > 5 && (
                      <div className="text-center py-2 text-muted-foreground">
                        ... and {tableData.length - 5} more records
                      </div>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/EnhancedRedeemCode.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { toast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Gift, 
  Plus, 
  Copy, 
  Check, 
  Calendar,
  User,
  Clock,
  Key
} from "lucide-react";

interface Plan {
  id: string;
  name: string;
  price: number;
  duration: string;
  features: string[];
  isActive: boolean;
}

interface RedeemCode {
  id: string;
  code: string;
  plan: {
    name: string;
  };
  duration: number;
  durationType: string;
  isUsed: boolean;
  usedBy?: {
    name: string;
    email: string;
  };
  usedAt?: string;
  createdAt: string;
  expiresAt?: string;
}

export function EnhancedRedeemCodeGenerator() {
  const [selectedPlan, setSelectedPlan] = useState("");
  const [duration, setDuration] = useState(1);
  const [durationType, setDurationType] = useState<"months" | "years">("months");
  const [codeCount, setCodeCount] = useState(1);
  const [copiedCode, setCopiedCode] = useState<string | null>(null);
  const [isGenerateDialogOpen, setIsGenerateDialogOpen] = useState(false);

  const queryClient = useQueryClient();

  const { data: plans, isLoading: plansLoading } = useQuery({
    queryKey: ["/api/plans"],
  });

  const { data: redeemCodes, isLoading: codesLoading } = useQuery({
    queryKey: ["/api/admin/redeem-codes"],
  });

  const generateCodesMutation = useMutation({
    mutationFn: async (data: { planName: string; duration: number; durationType: "months" | "years"; count: number }) => {
      return await apiRequest("/api/admin/redeem-codes/generate", {
        method: "POST",
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/redeem-codes"] });
      setIsGenerateDialogOpen(false);
      toast({
        title: "Success",
        description: `${codeCount} redeem code(s) generated successfully`,
      });
      // Reset form
      setSelectedPlan("");
      setDuration(1);
      setDurationType("months");
      setCodeCount(1);
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to generate redeem codes",
        variant: "destructive",
      });
    },
  });

  const copyToClipboard = async (code: string) => {
    try {
      await navigator.clipboard.writeText(code);
      setCopiedCode(code);
      setTimeout(() => setCopiedCode(null), 2000);
      toast({
        title: "Copied",
        description: "Redeem code copied to clipboard",
      });
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to copy code to clipboard",
        variant: "destructive",
      });
    }
  };

  const handleGenerate = () => {
    if (!selectedPlan || duration < 1 || codeCount < 1) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    const selectedPlanData = plans?.find((p: Plan) => p.name === selectedPlan);
    if (!selectedPlanData) {
      toast({
        title: "Error",
        description: "Selected plan not found",
        variant: "destructive",
      });
      return;
    }

    generateCodesMutation.mutate({
      planName: selectedPlan,
      duration,
      durationType,
      count: codeCount,
    });
  };

  return (
    <div className="space-y-6">
      {/* Generate New Codes */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Gift className="h-5 w-5" />
            Generate Redeem Codes
          </CardTitle>
        </CardHeader>
        <CardContent>
          <Dialog open={isGenerateDialogOpen} onOpenChange={setIsGenerateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="flex items-center gap-2" data-testid="button-open-generate-dialog">
                <Plus className="h-4 w-4" />
                Generate New Codes
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-md">
              <DialogHeader>
                <DialogTitle>Generate Redeem Codes</DialogTitle>
                <DialogDescription>
                  Create redeem codes for users to upgrade their subscriptions
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="plan-select">Plan</Label>
                  <Select value={selectedPlan} onValueChange={setSelectedPlan}>
                    <SelectTrigger data-testid="select-plan">
                      <SelectValue placeholder="Select a plan" />
                    </SelectTrigger>
                    <SelectContent>
                      {plans?.filter((plan: Plan) => plan.isActive).map((plan: Plan) => (
                        <SelectItem key={plan.id} value={plan.name}>
                          {plan.name} - ${plan.price/100}/month
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="duration">Duration</Label>
                    <Input
                      id="duration"
                      type="number"
                      min="1"
                      max={durationType === "years" ? 10 : 120}
                      value={duration}
                      onChange={(e) => setDuration(parseInt(e.target.value) || 1)}
                      data-testid="input-duration"
                    />
                  </div>
                  <div>
                    <Label htmlFor="duration-type">Type</Label>
                    <Select value={durationType} onValueChange={(value: "months" | "years") => setDurationType(value)}>
                      <SelectTrigger data-testid="select-duration-type">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="months">Months</SelectItem>
                        <SelectItem value="years">Years</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div>
                  <Label htmlFor="code-count">Number of Codes</Label>
                  <Input
                    id="code-count"
                    type="number"
                    min="1"
                    max="100"
                    value={codeCount}
                    onChange={(e) => setCodeCount(parseInt(e.target.value) || 1)}
                    data-testid="input-code-count"
                  />
                </div>

                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={() => setIsGenerateDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleGenerate}
                    disabled={generateCodesMutation.isPending}
                    data-testid="button-generate-codes"
                  >
                    {generateCodesMutation.isPending ? "Generating..." : "Generate"}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </CardContent>
      </Card>

      {/* Existing Codes */}
      <Card>
        <CardHeader>
          <CardTitle>Redeem Codes</CardTitle>
        </CardHeader>
        <CardContent>
          {codesLoading ? (
            <div className="animate-pulse space-y-4">
              {[1, 2, 3].map(i => (
                <div key={i} className="h-16 bg-gray-200 dark:bg-gray-700 rounded"></div>
              ))}
            </div>
          ) : !redeemCodes?.length ? (
            <div className="text-center py-8 text-muted-foreground">
              <Key className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No redeem codes found. Generate your first codes above.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {redeemCodes.map((code: RedeemCode) => (
                <div key={code.id} className="flex flex-col sm:flex-row sm:items-center justify-between p-4 border rounded-lg gap-4" data-testid={`redeem-code-${code.id}`}>
                  <div className="flex-1 space-y-2">
                    <div className="flex items-center gap-2 flex-wrap">
                      <code className="font-mono text-sm bg-gray-100 dark:bg-gray-800 px-2 py-1 rounded">
                        {code.code}
                      </code>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => copyToClipboard(code.code)}
                        className="h-8 w-8 p-0"
                        data-testid={`button-copy-${code.id}`}
                      >
                        {copiedCode === code.code ? (
                          <Check className="h-4 w-4 text-green-500" />
                        ) : (
                          <Copy className="h-4 w-4" />
                        )}
                      </Button>
                    </div>

                    <div className="flex items-center gap-4 text-sm text-muted-foreground flex-wrap">
                      <span className="flex items-center gap-1">
                        <Gift className="h-3 w-3" />
                        {code.plan.name}
                      </span>
                      <span className="flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        {code.duration} {code.durationType}
                      </span>
                      <span className="flex items-center gap-1">
                        <Clock className="h-3 w-3" />
                        Created {new Date(code.createdAt).toLocaleDateString()}
                      </span>
                    </div>

                    {code.isUsed && code.usedBy && (
                      <div className="flex items-center gap-2 text-sm">
                        <User className="h-3 w-3" />
                        <span>Used by {code.usedBy.name} ({code.usedBy.email})</span>
                        {code.usedAt && (
                          <span className="text-muted-foreground">
                            on {new Date(code.usedAt).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    )}
                  </div>

                  <div className="flex items-center gap-2">
                    <Badge variant={code.isUsed ? "secondary" : "default"}>
                      {code.isUsed ? "Used" : "Available"}
                    </Badge>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

